import {
  Container,
  Grid,
  List,
  ListItem,
  Fab,
  Avatar,
  ListItemAvatar,
  ListItemSecondaryAction,
  Menu,
  MenuItem,
  ListItemText,
  IconButton,
  Typography,
  Divider,
} from "@material-ui/core";
import {
  Help,
  HowToVote,
  AssessmentOutlined as Assessment,
  Add as AddIcon,
  Delete as DeleteIcon,
} from "@material-ui/icons";
import { makeStyles } from "@material-ui/core/styles";
import React from "react";
import { Link } from "react-router-dom";

const useStyles = makeStyles((theme) => ({
  root: {
    padding: "0 15 15",
  },
  demo: {
    backgroundColor: theme.palette.background.paper,
  },
  title: {
    margin: theme.spacing(2, 0, 2),
  },
  ListOverFlow: {
    overflowY: "auto",
    maxHeight: 450,
  },
  fab: {
    position: "fixed",
    bottom: theme.spacing(2),
    right: theme.spacing(2),
  },
  linkStyle: { color: "inherit", textDecorationLine: "inherit" },
  iconsColor: { color: "#3F5277" },
}));

const formList = [
  {
    id: 0,
    name: "Quiz 1",
    type: "quizzes",
    describtion: "Descritption 1",
  },
  {
    id: 1,
    name: "Survey 1",
    type: "surveys",
    describtion: "Descritption 2",
  },
  {
    id: 2,
    name: "Vote 1",
    type: "polls",
    describtion: "Descritption 3",
  },
  {
    id: 3,
    name: "Vote 2",
    type: "polls",
    describtion: "Descritption 4",
  },
  {
    id: 4,
    name: "Survey 2",
    type: "surveys",
    describtion: "Descritption 5",
  },
  {
    id: 5,
    name: "Survey 3",
    type: "surveys",
    describtion: "Descritption 6",
  },
  {
    id: 6,
    name: "Vote 3",
    type: "polls",
    describtion: "Descritption 7",
  },
  {
    id: 7,
    name: "Vote 4",
    type: "polls",
    describtion: "Descritption 8",
  },
];

export default function Home() {
  const classes = useStyles();
  const [anchorEl, setAnchorEl] = React.useState(null);

  const handleAddClick = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleAddClose = () => {
    setAnchorEl(null);
  };

  function deleteForm(e) {
    e.preventDefault();
    console.log(e.target);
  }

  return (
    <Container className={classes.root}>
      <Menu
        id="newFormMenu"
        anchorEl={anchorEl}
        keepMounted
        open={Boolean(anchorEl)}
        onClose={handleAddClose}
      >
        <Link to="/forms/quizzes/create" className={classes.linkStyle}>
          <MenuItem onClick={handleAddClose}>
            <Help className={classes.iconsColor} />
            &nbsp; Quiz
          </MenuItem>
        </Link>
        <Link to="/forms/surveys/create" className={classes.linkStyle}>
          <MenuItem onClick={handleAddClose}>
            <HowToVote className={classes.iconsColor} />
            &nbsp; Survey
          </MenuItem>
        </Link>
        <Link to="/forms/polls/create" className={classes.linkStyle}>
          <MenuItem onClick={handleAddClose}>
            <Assessment className={classes.iconsColor} />
            &nbsp; Poll/Vote
          </MenuItem>
        </Link>
      </Menu>
      <Grid
        container
        direction="row"
        spacing={1}
        justifyContent="center"
        alignItems="center"
      >
        <Grid item xs={12} md={6}>
          <Typography variant="h6" className={classes.title}>
            My Forms
          </Typography>
          <div className={classes.demo}>
            <List className={classes.ListOverFlow}>
              {formList.map((elementObj, index) => {
                let typeIcon = <Help className={classes.iconsColor} />;
                if (elementObj.type === "survey")
                  typeIcon = <HowToVote className={classes.iconsColor} />;
                else if (elementObj.type === "vote")
                  typeIcon = <Assessment className={classes.iconsColor} />;
                return (
                  <Link
                    to={`forms/${elementObj.type}/${elementObj.id}`}
                    className={classes.linkStyle}
                    key={elementObj.id}
                  >
                    <ListItem button>
                      <ListItemAvatar>
                        <Avatar title={elementObj.type}>{typeIcon}</Avatar>
                      </ListItemAvatar>
                      <ListItemText
                        primary={elementObj.id + "_" + elementObj.name}
                        secondary={elementObj.describtion}
                      />
                      <ListItemSecondaryAction>
                        <IconButton
                          edge="end"
                          aria-label="delete"
                          id={elementObj.id}
                          onClick={deleteForm}
                        >
                          <DeleteIcon />
                        </IconButton>
                      </ListItemSecondaryAction>
                    </ListItem>
                    {index !== formList.length - 1 ? <Divider /> : ""}
                  </Link>
                );
              })}
            </List>
          </div>
        </Grid>

        <Fab
          color="primary"
          className={classes.fab}
          aria-label="add"
          aria-controls="newFormMenu"
          aria-haspopup="true"
          onClick={handleAddClick}
        >
          <AddIcon />
        </Fab>
      </Grid>
    </Container>
  );
}
